const body = $input.first().json.body;
const leads = body.leads || [];
const finalContent = body.final_content || {};
const emailDraft = finalContent.email || null;

let subject = 'Automate Your Tax Compliance';
let bodyTemplate = 'Hi {{name}},\n\nI noticed that {{company}} might benefit from automating tax compliance.\n\nWould you be open to a quick call?\n\nBest regards';

if (emailDraft && emailDraft.subject) subject = emailDraft.subject;
if (emailDraft && emailDraft.body) bodyTemplate = emailDraft.body;

if (leads.length === 0) {
  leads.push({ name: 'Test User', email: 'workbyanii@gmail.com', company: 'Test Company' });
}

const emails = leads.map(lead => {
  let pBody = bodyTemplate
    .replace(/\{\{name\}\}/gi, lead.name)
    .replace(/\{\{company\}\}/gi, lead.company)
    .replace(/\{\{role\}\}/gi, lead.role || '');
  let pSubject = subject
    .replace(/\{\{name\}\}/gi, lead.name)
    .replace(/\{\{company\}\}/gi, lead.company);

  return { to: lead.email, name: lead.name, company: lead.company, subject: pSubject, body: pBody };
});

return emails.map(e => ({ json: e }));

const emails = $input.all();
const resumeUrl = $execution.resumeUrl;

let html = '<html><body style="font-family:Arial,sans-serif;max-width:800px;margin:0 auto;padding:20px;">';
html += '<h1 style="color:#1a73e8;">Lead Outreach Preview</h1>';
html += '<p>' + emails.length + ' personalized emails ready to send:</p>';

emails.forEach((item, i) => {
  const e = item.json;
  html += '<div style="background:#f8f9fa;border-left:3px solid #34a853;padding:15px;margin:15px 0;border-radius:0 8px 8px 0;">';
  html += '<p><strong>To:</strong> ' + e.name + ' (' + e.to + ')</p>';
  html += '<p><strong>Company:</strong> ' + e.company + '</p>';
  html += '<p><strong>Subject:</strong> ' + e.subject + '</p>';
  html += '<p style="white-space:pre-line;color:#5f6368;">' + e.body + '</p></div>';
});

html += '<div style="text-align:center;padding:30px;">';
html += '<a href="' + resumeUrl + '?action=send" style="display:inline-block;padding:14px 40px;margin:10px;border-radius:6px;text-decoration:none;font-weight:bold;font-size:16px;background:#34a853;color:white;">SEND ALL EMAILS</a>';
html += '<a href="' + resumeUrl + '?action=cancel" style="display:inline-block;padding:14px 40px;margin:10px;border-radius:6px;text-decoration:none;font-weight:bold;font-size:16px;background:#ea4335;color:white;">CANCEL</a>';
html += '</div></body></html>';

return [{ json: { email_html: html, emails: emails.map(e => e.json), total_leads: emails.length } }];
